import { Component, OnInit } from '@angular/core';
import { PdfMakeWrapper, Txt, Img } from 'pdfmake-wrapper';
import { StudentService } from 'src/app/services/student/student.service';
import { CourseService } from 'src/app/services/course/course.service';
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-certificate',
  templateUrl: './certificate.component.html',
  styleUrls: ['./certificate.component.css']
})
export class CertificateComponent implements OnInit {

  cc: string;
  bitCode: string;
  msm: string;
  constructor(private studentService: StudentService, private courseService: CourseService,
    private datePipe: DatePipe) { }

  ngOnInit() {
    this.msm = "Ingrese sus datos de estudiante";
  }

  generatePDF() {
    this.studentService.studentExistsByData(this.cc, this.bitCode).subscribe(x => {
      if (x) {
        const pdf = new PdfMakeWrapper();
        new Img('data:image/png;base64,')
          .height(100).width(400).alignment('center').opacity(0.20).build().then(img => {
            pdf.header(img);
            pdf.footer(img);
            pdf.add(
              new Txt("\n\n\n\n\n" + this.datePipe.transform(new Date(), 'yyyy-MM-dd'))
                .alignment("center").color("blue").bold().italics().end
            );
            pdf.add(
              new Txt("\n\n" + x.name + " " + x.lastname).alignment("center").color("blue")
                .bold().italics().end
            );
            pdf.add(
              new Txt("\n\nHa superado con xito el siguiente curso de BIT (Instituto)")
                .alignment("center").bold().italics().end
            );
            pdf.add(
              new Txt("\n\n" + x.course.name).alignment("center").color("blue").bold().italics().end
            );
            pdf.add(
              new Txt("\n" + x.course.description).alignment("center").bold().italics().end
            );
            pdf.add(
              new Txt("\n\n Duracion del curso: " + x.course.durationHours + " horas de estudio")
                .alignment('center').bold().italics().end
            );
          });
        //pdf.watermark(new Txt('watermark with Txt object').color('blue').end);
        pdf.info({
          title: 'A document',
          author: 'pdfmake-wrapper',
          subject: 'subject of document',
        });
        pdf.create().open();
        this.msm = "Ingrese sus datos de estudiante";
        this.bitCode = "";
        this.cc = "";
      } else {
        this.msm = "Estudiante no registrado, verifique los datos ingresados";
      }
    });
  }
}
